import React, { useState } from "react";

const Services = () => {
  const [selectedService, setSelectedService] = useState(null);

  const serviceList = [
    { emoji: "🩺", name: "Medical/Paramedical" },
    { emoji: "🛠", name: "Engineering" },
    { emoji: "💻", name: "Information Technology" },
    { emoji: "📈", name: "Business & Management" },
    { emoji: "👗", name: "Fashion Designing" },
    { emoji: "👗", name: "Modeling" },
    { emoji: "🎨", name: "Arts & Design" },
    { emoji: "🧑‍🏫", name: "Teacher" }
  ];

  // Sub-options for Medical/Paramedical and Engineering
  const medicalOptions = [
    "MBBS", "Dentist", "Nursing - BSc", "GNM", "ANM", 
    "Lab Technician", "Pharma - B.Pharm", "D.Pharm"
  ];

  const engineeringOptions = [
    "Mechanical", "Civil", "Electrical", "Computer Science", 
    "Electronics", "Automobile", "Aerospace"
  ];
  const teacherOptions= [
    "B-ed","D-ed"
  ];
  const businessManagementOptions = [
    "BBA", "BCom", "MBA", "MCom","CA"
  ];
  const fashionDesigningOptions = [
    "B.Des","M.Des","B.Ftech","M.Ftech"
  ];


  // Function to handle selection
  const handleSelect = (name) => {
    setSelectedService(selectedService === name ? null : name);
  };

  return (
    <section id="services" className="services">
      <h2>Our Services</h2>
      <p><u>Admission/Placement</u></p>

      <div className="service-buttons">
        {serviceList.map((service, index) => (
          <button
            key={index}
            className={`service-button ${selectedService === service.name ? "selected" : ""}`}
            onClick={() => handleSelect(service.name)}
          >
            {service.emoji} {service.name}
          </button>
        ))}
      </div>

      {/* Show medical/paramedical options */}
      {selectedService === "Medical/Paramedical" && (
        <div className="sub-options">
          {medicalOptions.map((option, index) => (
            <div key={index} className="sub-option">{option}</div>
          ))}
        </div>
      )}

      {/* Show engineering options */}
      {selectedService === "Engineering" && (
        <div className="sub-options">
          {engineeringOptions.map((option, index) => (
            <div key={index} className="sub-option">{option}</div>
          ))}
        </div>
      )}
      {/* Show Teacher options */}
      {selectedService === "Teacher" && (
        <div className="sub-options">
          {teacherOptions.map((option, index) => (
            <div key={index} className="sub-option">{option}</div>
          ))}
        </div>
      )}
      {/* Show Business & management options */}
      {selectedService === "Business & Management" && (
        <div className="sub-options">
          {businessManagementOptions.map((option, index) => (
            <div key={index} className="sub-option">{option}</div>
          ))}
        </div>
      )}

      {/* Show Fashion Designing options */}
      {selectedService === "Fashion Designing" && (
        <div className="sub-options">
          {fashionDesigningOptions.map((option, index) => (
            <div key={index} className="sub-option">{option}</div>
          ))}
        </div>
      )}

    </section>
  );
};

export default Services;
